SQL<pre><code> CREATE TABLE IF NOT EXISTS PERSONAGEM (
    idPersonagem SERIAL PRIMARY KEY,
    nome VARCHAR(40) NOT NULL,
    classe VARCHAR(40) NOT NULL
);
</code></pre>

SQL<pre><code>CREATE TABLE IF NOT EXISTS SHERIFF (
    idNpc INT PRIMARY KEY,
    delegacia VARCHAR(100),

    CONSTRAINT FK_SHERIFF_NPC FOREIGN KEY (idNpc) REFERENCES NPC(idNpc)
);
</code></pre>

SQL<pre><code>CREATE TABLE IF NOT EXISTS MUNDO (
    idMundo SERIAL PRIMARY KEY,
    nome VARCHAR(100) NOT NULL
);
</code></pre>

SQL<pre><code>CREATE TABLE IF NOT EXISTS GADO (
    idAnimal INT PRIMARY KEY,
    tipoGado VARCHAR(50),

    CONSTRAINT FK_GADO_ANIMAL FOREIGN KEY (idAnimal) REFERENCES ANIMAL(idAnimal)
);
</code></pre>

